import React from "react";

const ItemDueCard = ({ containers }) => {
  // BUILD LIST OF ALL TAKEN/RETURN ITEMS
const dueItems = [];

containers.forEach((rack) => {
  rack.slots.forEach((slot) => {
    (slot.items || []).forEach((item) => {
      (item.taken || []).forEach((taken) => {
        const takenDate = taken.date || "-";
        const returnDate = taken.returnDate || "-";

        // Calculate overdue days
        let overdueDays = 0;
        if (returnDate !== "-") {
          const diff =
            (new Date() - new Date(returnDate)) / (1000 * 60 * 60 * 24);
          overdueDays = Math.max(0, Math.floor(diff));
        }

        dueItems.push({
          itemName: item.name,
          takenBy: taken.name,
          takenDate,
          returnDate,
          overdue: overdueDays,
        });
      });
    });
  });
});

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
        height: "100%",
      }}
    >
      <div className="card-header fw-bold" style={{ borderColor: "hsl(215,25%,25%)" }}>
        Item Due Dates
      </div>

      <div
        className="card-body"
        style={{
          maxHeight: "320px",
          overflowY: "auto",
          paddingRight: "6px",
        }}
      >
        <table
  className="table table-sm"
  style={{
    color: "white",
    background: "hsl(215, 25%, 14%)",
  }}
>
  <thead>
    <tr style={{ background: "hsl(215, 25%, 18%)" }}>
      <th>Item</th>
      <th>Taken By</th>
      <th>Taken</th>
      <th>Return</th>
      <th>Overdue</th>
      <th></th>
    </tr>
  </thead>

  <tbody>
    {dueItems.map((row, idx) => (
      <tr
        key={idx}
        style={{
          background:
            idx % 2 === 0
              ? "hsl(215, 25%, 16%)"
              : "hsl(215, 25%, 18%)",
        }}
      >
        <td>{row.itemName}</td>
        <td>{row.takenBy}</td>
        <td>{row.takenDate}</td>
        <td>{row.returnDate}</td>
        <td>{row.overdue} days</td>
        <td>
          <button className="btn btn-primary btn-sm">Reminder</button>
        </td>
      </tr>
    ))}
  </tbody>
</table>

      </div>
    </div>
  );
};

export default ItemDueCard;
