// src/pages/manager/ManagerDashboard.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import LowStockCard from "../../components/admin/dashboard/LowStockCard";
import ItemDueCard from "../../components/admin/dashboard/DueDatesCard";
import ContainerSummaryCard from "../../components/admin/dashboard/ContainerSummaryCard";

import ManagerPermissionModal from "../../components/manager/ManagerPermissionModal";

const ManagerDashboard = ({ containers = [], approvedPermissions = [], onMarkGiven }) => {
  const navigate = useNavigate();

  const [selectedReq, setSelectedReq] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // manager jump to racks page slot
  const jumpToSlot = (containerId, slotNumber) => {
    navigate(`/manager/racks?jumpRack=${containerId}&jumpSlot=${slotNumber}`);
  };

  const handleOpen = (req) => {
    setSelectedReq(req);
    setShowModal(true);
  };

  const handleMarkGiven = (id) => {
    // call parent App handler
    if (onMarkGiven) onMarkGiven(id);
    // modal will be closed by the modal itself
  };

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">Manager Dashboard</h2>
      <p className="text-muted">Overview of racks, items and approvals</p>

      {/* Approved Requests */}
      <div
        className="card mb-4 p-3"
        style={{
          background: "hsl(215, 25%, 12%)",
          border: "1px solid hsl(215, 20%, 25%)",
          color: "white",
        }}
      >
        <div className="card-header fw-bold">Approved Requests</div>

        <div className="card-body" style={{ maxHeight: 260, overflowY: "auto" }}>
          {approvedPermissions.length === 0 ? (
            <p className="text-muted">No approved permissions yet.</p>
          ) : (
            approvedPermissions.map((req) => (
              <div
                key={req.id}
                className="d-flex align-items-center rounded mb-2 p-3"
                style={{
                  background: "hsl(215, 25%, 14%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                  cursor: "pointer",
                }}
                onClick={() => handleOpen(req)}
              >
                {/* Left: name & email */}
                <div style={{ flex: "0 0 28%", minWidth: 160 }}>
                  <div className="fw-semibold">{req.userName}</div>
                  <div className="text-muted small">{req.userEmail}</div>
                </div>

                {/* Center: Item name */}
                <div style={{ flex: "1 1 auto", textAlign: "center" }}>
                  <div className="text-info fw-semibold">{req.itemName}</div>
                </div>

                {/* Right: view + qty */}
                <div style={{ flex: "0 0 160px", textAlign: "right" }}>
                  <div
                    className="text-primary"
                    style={{ cursor: "pointer", fontWeight: 600 }}
                  >
                    View Details â†’
                  </div>
                  <div className="fw-semibold">Qty: {req.quantity}</div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Rack Overview */}
      <div className="row g-3 mb-4">
        {containers.map((rack) => (
          <div className="col-12 col-sm-6 col-lg-4" key={rack.id}>
            <ContainerSummaryCard container={rack} />
          </div>
        ))}
      </div>

      {/* Low Stock + Due Items */}
      <div className="row g-4">
        <div className="col-12 col-lg-6">
          <ItemDueCard containers={containers} />
        </div>

        <div className="col-12 col-lg-6">
          <LowStockCard containers={containers} onJumpToSlot={jumpToSlot} />
        </div>
      </div>

      {/* Permission Modal */}
      <ManagerPermissionModal
        show={showModal}
        onClose={() => setShowModal(false)}
        request={selectedReq}
        onGiven={handleMarkGiven}
      />
    </div>
  );
};

export default ManagerDashboard;
