import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Check, X, Mail, Calendar } from "lucide-react";

const UserApproval = ({ pendingUsers, onApprove, onReject }) => {
  const handleApprove = (user) => {
    onApprove(user.id);
    alert(`${user.name} has been approved successfully.`);
  };

  const handleReject = (user) => {
    onReject(user.id);
    alert(`${user.name} has been rejected.`);
  };

  // ✅ No pending users
  if (!pendingUsers || pendingUsers.length === 0) {
    return (
      <div className="card shadow-sm my-3">
        <div className="card-header bg-light">
          <h5 className="mb-0">Pending User Approvals</h5>
        </div>
        <div className="card-body text-center text-muted py-4">
          No pending approvals
        </div>
      </div>
    );
  }

  // ✅ Pending users list
  return (
    <div className="card shadow-sm my-3">
      {/* Header */}
      <div className="card-header d-flex justify-content-between align-items-center bg-light">
        <h5 className="mb-0">Pending User Approvals</h5>
        <span className="badge bg-secondary">{pendingUsers.length} pending</span>
      </div>

      {/* Card Content */}
      <div className="card-body">
        {pendingUsers.map((user) => (
          <div
            key={user.id}
            className="d-flex justify-content-between align-items-center border rounded p-3 mb-3 bg-light shadow-sm"
          >
            {/* Left: User Info */}
            <div className="flex-grow-1">
              <p className="fw-semibold mb-1">{user.name}</p>
              <div className="d-flex flex-wrap gap-3 text-muted small">
                <span className="d-flex align-items-center gap-1">
                  <Mail size={14} />
                  {user.email}
                </span>
                <span className="d-flex align-items-center gap-1">
                  <Calendar size={14} />
                  {user.registeredDate}
                </span>
              </div>
            </div>

            {/* Right: Action Buttons */}
            <div className="d-flex align-items-center gap-2">
              <button
                className="btn btn-success btn-sm d-flex align-items-center gap-1"
                onClick={() => handleApprove(user)}
              >
                <Check size={16} />
                Approve
              </button>
              <button
                className="btn btn-danger btn-sm d-flex align-items-center gap-1"
                onClick={() => handleReject(user)}
              >
                <X size={16} />
                Reject
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserApproval;
