import React, { useState } from "react";
import RequestItemModal from "../../components/users/RequestItemModal";

/**
 * Props passed from App:
 *  - permissionRequests (array)
 *  - setPermissionRequests (function)
 *  - containers (optional if you want to display racks)
 */
const UserDashboard = ({ permissionRequests = [], setPermissionRequests, containers = [] }) => {
  // current user (hard-coded for now)
  const currentUserEmail = "user@gmail.com";
  const currentUserName = "User A";

  const myRequests = permissionRequests.filter((r) => r.userEmail === currentUserEmail);

  const [showModal, setShowModal] = useState(false);

  const handleSubmitRequest = (req) => {
    setPermissionRequests((prev) => [req, ...prev]);
  };

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">My Dashboard</h2>
      <p className="text-muted">Submit requests & view your request history</p>

      <div className="d-flex gap-2 mb-3">
        <button className="btn btn-primary" onClick={() => setShowModal(true)}>Request Item</button>
      </div>

      <div className="card shadow-sm" style={{ background: "hsl(215,25%,12%)", color: "white" }}>
        <div className="card-header fw-bold">My Requests</div>

        <div className="card-body" style={{ maxHeight: 360, overflowY: "auto" }}>
          {myRequests.length === 0 ? (
            <p className="text-muted">You have no requests yet.</p>
          ) : (
            myRequests.map((r) => (
              <div key={r.id} className="d-flex justify-content-between align-items-center p-3 mb-2 rounded" style={{ background: "hsl(215,25%,16%)", border: "1px solid hsl(215,20%,25%)" }}>
                <div>
                  <div className="fw-semibold">{r.itemName} <small className="text-muted">({r.quantity})</small></div>
                  <div className="text-muted small">{r.dateRequested} â€¢ {r.status}</div>
                </div>
                <div className="text-end">
                  <div className="small text-muted">{r.whichProject}</div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <RequestItemModal
        show={showModal}
        onClose={() => setShowModal(false)}
        onSubmit={handleSubmitRequest}
        currentUser={{ name: currentUserName, email: currentUserEmail }}
      />
    </div>
  );
};

export default UserDashboard;
