// components/admin/dashboard/ItemDueCard.jsx
import React from "react";

const ItemDueCard = ({ containers = [] }) => {
  const rows = [];

  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      (slot.items || []).forEach((item) => {
        (item.taken || []).forEach((t) => {
          const today = new Date();
          const returnDate = t.returnDate ? new Date(t.returnDate) : null;

          let overdue = 0;
          if (returnDate) {
            overdue = Math.max(
              0,
              Math.floor((today - returnDate) / (1000 * 60 * 60 * 24))
            );
          }

          rows.push({
            itemName: item.name,
            user: t.user,
            takenDate: t.date,
            returnDate: t.returnDate || "-",
            overdue,
          });
        });
      });
    });
  });


   return (
 <div
  className="card-body"
  style={{
    maxHeight: "320px",
    overflowY: "auto",
    paddingRight: "6px",
  }}
>
  <div
    style={{
      borderRadius: 8,
      overflow: "hidden",
      border: "1px solid hsl(215,20%,20%)",
      background: "hsl(215,25%,14%)", // FORCE DARK BG
    }}
  >
    <table
      className="table table-sm mb-0"
      style={{
        width: "100%",
        background: "hsl(215,25%,14%)", // FORCE DARK (top)
        color: "white",
      }}
    >
      <thead
        style={{
          background: "hsl(215, 25%, 18%)",
          color: "white",
          position: "sticky",
          top: 0,
          zIndex: 5,
        }}
      >
        <tr>
          <th style={{ padding: 10 }}>Item</th>
          <th style={{ padding: 10 }}>Taken By</th>
          <th style={{ padding: 10 }}>Taken</th>
          <th style={{ padding: 10 }}>Return</th>
          <th style={{ padding: 10 }}>Overdue</th>
          <th></th>
        </tr>
      </thead>

      <tbody>
        {rows.length === 0 ? (
          <tr>
            <td
              colSpan="6"
              className="text-center"
              style={{
                padding: 20,
                color: "#b8bdc5",
                background: "hsl(215,25%,14%)",
              }}
            >
              No due items
            </td>
          </tr>
        ) : (
          rows.map((r, i) => (
            <tr
              key={i}
              style={{
                background:
                  i % 2 === 0
                    ? "hsl(215,25%,16%)"
                    : "hsl(215,25%,18%)", // NO WHITE SHADE
              }}
            >
              <td style={{ padding: 10 }}>{r.itemName}</td>
              <td style={{ padding: 10 }}>{r.user}</td>
              <td style={{ padding: 10 }}>{r.takenDate}</td>
              <td style={{ padding: 10 }}>{r.returnDate}</td>
              <td style={{ padding: 10 }}>{r.overdue} days</td>
              <td style={{ padding: 10 }}>
                <button className="btn btn-primary btn-sm">Reminder</button>
              </td>
            </tr>
          ))
        )}
      </tbody>
    </table>
  </div>
</div>

);

};

export default ItemDueCard;
