import { Modal, Button, Form } from "react-bootstrap";

/**
 * Small status tag component (keeps same colors you used previously)
 */
const StatusTag = ({ status }) => {
  const base = {
    padding: "4px 10px",
    borderRadius: 8,
    fontSize: 12,
    fontWeight: 700,
    display: "inline-block",
  };
  if (status === "Pending")
    return (
      <span style={{ ...base, background: "hsl(45,100%,65%)", color: "#222" }}>
        {status}
      </span>
    );
  if (status === "Approved")
    return (
      <span style={{ ...base, background: "hsl(140,40%,30%)", color: "white" }}>
        {status}
      </span>
    );
  return (
    <span style={{ ...base, background: "hsl(10,80%,40%)", color: "white" }}>
      {status}
    </span>
  );
};

/**
 * UserDashboard - design A
 *
 * Props:
 * - permissionRequests: array of requests (for current user)
 * - onCreateRequest(formData) -> to add new request
 * - onCancelRequest(requestId) -> to cancel pending request
 * - currentUser: { name, email, phone?, role? }
 */
const UserDashboard = ({
  permissionRequests = [],
  onCreateRequest,
  onCancelRequest,
  currentUser,
}) => {
  const [showNewModal, setShowNewModal] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const [selectedReq, setSelectedReq] = useState(null);

  const [form, setForm] = useState({
    itemName: "",
    quantity: 1,
    projectName: "",
    returnable: false,
    returnDate: "",
    message: "",
  });

  const openDetails = (req) => {
    setSelectedReq(req);
    setShowDetails(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.itemName || !form.quantity || !form.projectName) {
      alert("Please fill required fields");
      return;
    }

    // Normalize quantity to number
    onCreateRequest({ ...form, quantity: Number(form.quantity) });

    // reset & close
    setForm({
      itemName: "",
      quantity: 1,
      projectName: "",
      returnable: false,
      returnDate: "",
      message: "",
    });
    setShowNewModal(false);
  };

  const handleCancelFromCard = (id) => {
    if (!window.confirm("Cancel this pending request?")) return;
    onCancelRequest(id);
  };

  const handleCancelFromDetails = (id) => {
    if (!window.confirm("Cancel this pending request?")) return;
    onCancelRequest(id);
    setShowDetails(false);
  };

  // Compact card UI (Design A)
  const CardCompact = ({ req }) => {
    return (
      <div
        className="card mb-2 p-2"
        style={{
          background: "hsl(215,25%,16%)",
          border: "1px solid hsl(215,20%,25%)",
          color: "white",
          cursor: "pointer",
          borderRadius: 8,
        }}
        onClick={() => openDetails(req)}
      >
        <div className="d-flex align-items-center justify-content-between">
          {/* Left: item + project */}
          <div>
            <div className="fw-bold" style={{ fontSize: 15 }}>
              {req.itemName}
            </div>
            <div className="text-muted small" style={{ marginTop: 2 }}>
              {req.whichProject}
            </div>
          </div>

          {/* Middle: qty */}
          <div style={{ textAlign: "center", minWidth: 80 }}>
            <div className="text-muted small">Qty</div>
            <div className="fw-bold" style={{ fontSize: 16 }}>
              {req.quantity}
            </div>
          </div>

          {/* Right: status */}
          <div style={{ textAlign: "right", minWidth: 140 }}>
            <div style={{ marginBottom: 6 }}>
              <StatusTag status={req.status} />
            </div>
            <div className="text-muted small">Requested: {req.dateRequested}</div>

            {/* Cancel action appears inline as small button if Pending */}
            {req.status === "Pending" && (
              <div style={{ marginTop: 8 }}>
                <Button
                  size="sm"
                  variant="danger"
                  onClick={(e) => {
                    e.stopPropagation(); // prevent opening details
                    handleCancelFromCard(req.id);
                  }}
                >
                  Cancel
                </Button>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-start mb-3">
        <div>
          <p className="text-muted">User Dashboard</p>
          <h2 className="fw-bold mb-1">Welcome, {currentUser?.name || "User"}</h2>
        </div>

        <div>
          <Button onClick={() => setShowNewModal(true)} className="btn-primary">
            Request New Item
          </Button>
        </div>
      </div>

      {/* My Requests Header Card */}
      <div
        className="card p-3 mb-4"
        style={{
          background: "hsl(215,25%,12%)",
          border: "1px solid hsl(215,20%,25%)",
          color: "white",
        }}
      >
        <h5 className="fw-bold mb-3">My Requests</h5>

        {permissionRequests.length === 0 ? (
          <p className="text-muted">You have not made any requests yet.</p>
        ) : (
          permissionRequests.map((req) => (
            <CardCompact key={req.id} req={req} />
          ))
        )}
      </div>

      {/* ------------------ DETAILS MODAL ------------------ */}
      <Modal
        show={showDetails}
        onHide={() => setShowDetails(false)}
        centered
        size="lg"
      >
        <Modal.Header
          closeButton
          style={{
            background: "hsl(215,25%,12%)",
            color: "white",
            borderBottom: "1px solid hsl(215,20%,25%)",
          }}
        >
          <Modal.Title>Request Details</Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ background: "hsl(215,25%,14%)", color: "white" }}>
          {selectedReq ? (
            <>
              <div className="row">
                <div className="col-md-8">
                  <h5 className="fw-bold mb-1">{selectedReq.itemName}</h5>
                  <div className="text-muted small mb-2">
                    Project: {selectedReq.whichProject}
                  </div>
                  <div style={{ marginBottom: 8 }}>
                    <strong>Message:</strong>
                    <div
                      style={{
                        marginTop: 6,
                        background: "hsl(215,25%,12%)",
                        padding: 10,
                        borderRadius: 6,
                        border: "1px solid hsl(215,20%,25%)",
                      }}
                    >
                      {selectedReq.message || "No message provided."}
                    </div>
                  </div>

                  <div className="d-flex gap-3 mt-3">
                    <div>
                      <strong>Quantity:</strong> {selectedReq.quantity}
                    </div>
                    <div>
                      <strong>Request Date:</strong> {selectedReq.dateRequested}
                    </div>
                    {selectedReq.itemType === "Returnable" && (
                      <div>
                        <strong>Return Date:</strong> {selectedReq.returnDate}
                      </div>
                    )}
                  </div>
                </div>

                <div className="col-md-4 text-end">
                  <div style={{ marginBottom: 8 }}>
                    <StatusTag status={selectedReq.status} />
                  </div>
                  <div className="text-muted small mb-2">
                    {selectedReq.userName}
                    <br />
                    <span className="text-muted">{selectedReq.userEmail}</span>
                  </div>

                  {/* Cancel if pending */}
                  {selectedReq.status === "Pending" && (
                    <Button
                      variant="danger"
                      onClick={() => handleCancelFromDetails(selectedReq.id)}
                    >
                      Cancel Request
                    </Button>
                  )}
                </div>
              </div>
            </>
          ) : (
            <p className="text-muted">No request selected.</p>
          )}
        </Modal.Body>

        <Modal.Footer
          style={{
            background: "hsl(215,25%,12%)",
            borderTop: "1px solid hsl(215,20%,25%)",
          }}
        >
          <Button variant="secondary" onClick={() => setShowDetails(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      {/* ------------------ NEW REQUEST MODAL ------------------ */}
      <Modal
        show={showNewModal}
        onHide={() => setShowNewModal(false)}
        centered
      >
        <Modal.Header
          closeButton
          style={{
            background: "hsl(215,25%,12%)",
            color: "white",
            borderBottom: "1px solid hsl(215,20%,25%)",
          }}
        >
          <Modal.Title>Request New Item</Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ background: "hsl(215,25%,14%)", color: "white" }}>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-2">
              <Form.Label>Item Name *</Form.Label>
              <Form.Control
                value={form.itemName}
                onChange={(e) => setForm({ ...form, itemName: e.target.value })}
                style={{
                  background: "hsl(215,25%,12%)",
                  color: "white",
                  border: "1px solid hsl(215,20%,25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Quantity *</Form.Label>
              <Form.Control
                type="number"
                min={1}
                value={form.quantity}
                onChange={(e) =>
                  setForm({ ...form, quantity: Number(e.target.value) })
                }
                style={{
                  background: "hsl(215,25%,12%)",
                  color: "white",
                  border: "1px solid hsl(215,20%,25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Project Name *</Form.Label>
              <Form.Control
                value={form.projectName}
                onChange={(e) =>
                  setForm({ ...form, projectName: e.target.value })
                }
                style={{
                  background: "hsl(215,25%,12%)",
                  color: "white",
                  border: "1px solid hsl(215,20%,25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="mb-2 d-flex align-items-center">
              <Form.Check
                type="checkbox"
                label="Returnable?"
                checked={form.returnable}
                onChange={(e) =>
                  setForm({ ...form, returnable: e.target.checked })
                }
              />
              {form.returnable && (
                <input
                  type="date"
                  className="form-control ms-2"
                  value={form.returnDate}
                  onChange={(e) =>
                    setForm({ ...form, returnDate: e.target.value })
                  }
                  style={{
                    maxWidth: 220,
                    background: "hsl(215,25%,12%)",
                    color: "white",
                    border: "1px solid hsl(215,20%,25%)",
                  }}
                />
              )}
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Message (optional)</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={form.message}
                onChange={(e) => setForm({ ...form, message: e.target.value })}
                style={{
                  background: "hsl(215,25%,12%)",
                  color: "white",
                  border: "1px solid hsl(215,20%,25%)",
                }}
              />
            </Form.Group>

            <div className="d-flex gap-2 justify-content-end mt-3">
              <Button
                variant="secondary"
                onClick={() => setShowNewModal(false)}
              >
                Close
              </Button>
              <Button variant="primary" type="submit">
                Submit Request
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default UserDashboard;
