// components/admin/dashboard/ItemDueCard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const ItemDueCard = () => {
  const [rows, setRows] = useState([]);

  const fetchDueDates = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8000/api/manager/dueDates", {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      if (res.data.data) {
        setRows(res.data.data);
      }
    } catch (err) {
      console.error("Error fetching due items:", err);
    }
  };

  useEffect(() => {
    fetchDueDates();
  }, []);

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("en-IN") : "-";

  return (
    <div
    className="card shadow-sm"
    style={{
      background: "hsl(215, 25%, 12%)",
      border: "1px solid hsl(215, 20%, 25%)",
      color: "white",
      height: "100%",
      display: "flex",
      flexDirection: "column",
    }}
  >
    <div
      className="card-header fw-bold"
      style={{
        borderColor: "hsl(215,25%,25%)",
        background: "hsl(215, 25%, 18%)",
      }}
    >
      Item Due Dates
    </div>

    <div
      className="card-body p-0"
      style={{
        flex: 1,
        overflowY: "auto",
      }}
    >
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr
            style={{
              background: "hsl(215, 25%, 18%)",
              position: "sticky",
              top: 0,
              zIndex: 5,
            }}
          >
            {["Item", "User", "Taken", "Return", "Days Left", "Overdue", ""].map(
              (h, i) => (
                <th
                  key={i}
                  style={{
                    padding: "10px 12px",
                    fontWeight: 700,
                    textAlign: "left",
                    borderBottom: "1px solid hsl(215,25%,25%)",
                  }}
                >
                  {h}
                </th>
              )
            )}
          </tr>
        </thead>

        <tbody>
          {rows.length === 0 ? (
            <tr>
              <td
                colSpan={7}
                style={{
                  padding: 20,
                  textAlign: "center",
                  color: "#bbb",
                }}
              >
                No due items
              </td>
            </tr>
          ) : (
            rows.map((r, i) => (
              <tr
                key={r.borrowId}
                style={{
                  background:
                    i % 2 === 0
                      ? "hsl(215, 25%, 14%)"
                      : "hsl(215, 25%, 16%)",
                }}
              >
                <td style={{ padding: "10px 12px" }}>{r.itemName}</td>
                <td style={{ padding: "10px 12px" }}>{r.userName}</td>
                <td style={{ padding: "10px 12px" }}>
                  {formatDate(r.takenDate)}
                </td>
                <td style={{ padding: "10px 12px" }}>
                  {formatDate(r.returnDate)}
                </td>
                <td style={{ padding: "10px 12px" }}>
                  {r.daysLeft ?? "-"}
                </td>
                <td
                  style={{
                    padding: "10px 12px",
                    color:
                      r.overdueDays > 0 ? "#ff5555" : "rgb(190,190,190)",
                  }}
                >
                  {r.overdueDays ?? 0} days
                </td>
                <td style={{ padding: "10px 12px" }}>
                  <button className="btn btn-primary btn-sm">
                    Reminder
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  </div>
  );
};

export default ItemDueCard;
