import React, { useState, useEffect } from "react";
import { Form, Button } from "react-bootstrap";
import { CircleUserRound, PenSquare } from "lucide-react";
import { useOutletContext } from "react-router-dom";
import api from "../../api/axios";
import NotificationToast from "../../components/common/NotificationToast";

const UserProfile = () => {
  const { currentUser } = useOutletContext();

  const [editMode, setEditMode] = useState(false);
  const [editPassword, setEditPassword] = useState(false);

  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    role: "user",
  });

  const [passwordForm, setPasswordForm] = useState({
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  // Load user data
  useEffect(() => {
    if (currentUser) {
      setForm({
        name: currentUser.name || "",
        email: currentUser.email || "",
        phone: currentUser.phone || "",
        role: currentUser.role || "user",
      });
    }
  }, [currentUser]);

  // Update profile (name + phone)
  const handleSave = async () => {
    if (!form.name || !form.phone) {
      setToast({
        show: true,
        message: "Name and phone are required",
        bg: "warning",
      });
      return;
    }

    try {
      const res = await api.put("/users/update", {
        name: form.name,
        phone: form.phone,
      });

      setForm((prev) => ({
        ...prev,
        name: res.data.data.name,
        phone: res.data.data.phone,
      }));

      setToast({
        show: true,
        message: "Profile updated successfully",
        bg: "success",
      });

      setEditMode(false);
    } catch (err) {
      setToast({
        show: true,
        message: "Profile update failed",
        bg: "danger",
      });
    }
  };

  // Update password
  const handlePasswordUpdate = async () => {
    const { oldPassword, newPassword, confirmPassword } = passwordForm;

    if (!oldPassword || !newPassword || !confirmPassword) {
      setToast({
        show: true,
        message: "Please fill all password fields",
        bg: "warning",
      });
      return;
    }

    if (newPassword !== confirmPassword) {
      setToast({
        show: true,
        message: "Passwords do not match",
        bg: "danger",
      });
      return;
    }

    try {
      await api.put("/users/change-password", {
        oldPassword,
        newPassword,
      });

      setToast({
        show: true,
        message: "Password updated successfully",
        bg: "success",
      });

      setPasswordForm({
        oldPassword: "",
        newPassword: "",
        confirmPassword: "",
      });
      setEditPassword(false);
    } catch (err) {
      setToast({
        show: true,
        message: err.response?.data?.message || "Password update failed",
        bg: "danger",
      });
    }
  };

  return (
    <div className="container my-4">
      <NotificationToast
        show={toast.show}
        message={toast.message}
        bg={toast.bg}
        onClose={() => setToast((p) => ({ ...p, show: false }))}
      />

      <div
        className="card mx-auto"
        style={{
          maxWidth: 700,
          background: "#0F1623",
          color: "white",
          borderRadius: 14,
          border: "1px solid #2A3A4B",
          boxShadow: "0 6px 30px rgba(0,0,0,0.45)",
        }}
      >
        {/* Header */}
        <div
          className="d-flex align-items-center p-4"
          style={{ borderBottom: "1px solid #2A3A4B" }}
        >
          <div
            className="rounded-circle d-flex justify-content-center align-items-center"
            style={{
              width: 54,
              height: 54,
              background: "#1C2736",
              border: "2px solid #2A3A4B",
              marginRight: 20,
            }}
          >
            <CircleUserRound size={30} />
          </div>

          <div style={{ flex: 1 }}>
            <div className="fw-bold" style={{ fontSize: "1.25rem" }}>
              {form.name}
            </div>
            <div style={{ opacity: 0.7 }}>{form.role}</div>
          </div>

          <Button variant="primary" onClick={() => setEditMode(!editMode)}>
            {editMode ? "Done" : "Edit"}
          </Button>
        </div>

        {/* Profile Fields */}
        <Form className="p-4">
          <div className="row">
            <div className="col-md-6 mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control value={form.email} disabled />
            </div>

            <div className="col-md-6 mb-3">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                value={form.phone}
                disabled={!editMode}
                onChange={(e) =>
                  setForm({ ...form, phone: e.target.value })
                }
              />
            </div>

            <div className="col-md-6 mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                value={form.name}
                disabled={!editMode}
                onChange={(e) =>
                  setForm({ ...form, name: e.target.value })
                }
              />
            </div>

            {/* Password Field with Pen Icon */}
            <div className="col-md-6 mb-3">
              <Form.Label>Password</Form.Label>
              <div style={{ position: "relative" }}>
                <Form.Control
                  type="password"
                  value="********"
                  disabled
                  style={{ paddingRight: "40px" }}
                />
                <PenSquare
                  size={18}
                  style={{
                    position: "absolute",
                    right: 12,
                    top: "50%",
                    transform: "translateY(-50%)",
                    cursor: "pointer",
                    opacity: 0.85,
                  }}
                  onClick={() => setEditPassword(true)}
                />
              </div>
            </div>
          </div>

          {editMode && (
            <div className="d-flex justify-content-end gap-2">
              <Button variant="secondary" onClick={() => setEditMode(false)}>
                Cancel
              </Button>
              <Button variant="success" onClick={handleSave}>
                Save
              </Button>
            </div>
          )}
        </Form>

        {/* Password Edit Section */}
        {editPassword && (
          <div
            className="p-4"
            style={{ borderTop: "1px solid #2A3A4B" }}
          >
            <h6 className="mb-3">Update Password</h6>

            <div className="row">
              <div className="col-md-6 mb-3">
                <Form.Label>Old Password</Form.Label>
                <Form.Control
                  type="password"
                  value={passwordForm.oldPassword}
                  onChange={(e) =>
                    setPasswordForm({
                      ...passwordForm,
                      oldPassword: e.target.value,
                    })
                  }
                />
              </div>

              <div className="col-md-6 mb-3">
                <Form.Label>New Password</Form.Label>
                <Form.Control
                  type="password"
                  value={passwordForm.newPassword}
                  onChange={(e) =>
                    setPasswordForm({
                      ...passwordForm,
                      newPassword: e.target.value,
                    })
                  }
                />
              </div>

              <div className="col-md-6 mb-3">
                <Form.Label>Confirm New Password</Form.Label>
                <Form.Control
                  type="password"
                  value={passwordForm.confirmPassword}
                  onChange={(e) =>
                    setPasswordForm({
                      ...passwordForm,
                      confirmPassword: e.target.value,
                    })
                  }
                />
              </div>
            </div>

            <div className="d-flex justify-content-end gap-2">
              <Button
                variant="secondary"
                onClick={() => {
                  setEditPassword(false);
                  setPasswordForm({
                    oldPassword: "",
                    newPassword: "",
                    confirmPassword: "",
                  });
                }}
              >
                Cancel
              </Button>
              <Button variant="warning" onClick={handlePasswordUpdate}>
                Update Password
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
